import { useEffect, useState } from 'react'
import axios from 'axios'

const Filter = ({ filterChange }) => {
  return (
    <div>
      <p>
        filter term <input onChange={filterChange}></input>
      </p>
    </div>
  )
}

const Countries = ({ filtered, setShow }) => {
  if (filtered.length === 1) {
    const country = filtered[0]
    console.log(country)
    return (
      <div>
        <h1>{country.name}</h1>
        <div>capital {country.capital}</div>
        <div>area {country.area}</div>

        <h2>languages:</h2>
        <ul>
          {country.languages.map((language) =>
            <li key={language.name}>
              {language.name}
            </li>
          )}
        </ul>
        <img src={country.flag} width="300" height="250"/>
      </div>
    )
  }

  if (filtered.length > 10) {
    return (
      <div>
        Too many matches, be more specific
      </div>
    )
  }
  return (
    <div>
      {filtered.map((country) =>
        <div key={country.name}>
          {country.name} <button onClick={() => setShow(country.name)}>
            show
          </button>
        </div>
      )}
    </div>
  )
}

const App = () => {
  const [countries, setCountries] = useState([])
  const [show, setShow] = useState('')

  const hook = () => {
    axios
      .get('https://restcountries.com/v2/all')
      .then(response => {
        setCountries(response.data)
      })
  }

  useEffect(hook, [])
  const activeFilter = (show === '') ? countries : countries.filter(country => country.name.toLowerCase().indexOf(show.toLowerCase()) >= 0)

  const handleFilterChange = (event) => {
    setShow(event.target.value)
  }

  return (
    <div>
      <Filter filterChange={handleFilterChange} />
      <Countries filtered={activeFilter} setShow={setShow}/>
    </div>
  )

}

export default App
