import { useEffect, useState } from 'react'
import axios from 'axios'

const Filter = ({ filterChange }) => {
  return (
    <div>
      <p>
        filter term <input onChange={filterChange}></input>
      </p>
    </div>
  )
}

const Countries = ({ filtered }) => {
  if (filtered.length === 1) {
    const country = filtered[0]
    console.log(country)
    return (
      <div>
        <h1>{country.name}</h1>
        <div>{country.capital}</div>
        <div>{country.area}</div>

        <p>img src={country.flag}</p>
      </div>
    )
  }

  if (filtered.length > 10) {
    return (
      <div>
        Too many matches, be more specific
      </div>
    )
  }
  return (
    <div>
      {filtered.map((country) =>
        <div key={country.name}>
          {country.name}
        </div>
      )}
    </div>
  )
}

const App = () => {
  const [countries, setCountries] = useState([])
  const [show, setShow] = useState('')

  const hook = () => {
    axios
      .get('https://restcountries.com/v2/all')
      .then(response => {
        setCountries(response.data)
      })
  }

  useEffect(hook, [])
  const activeFilter = (show === '') ? countries : countries.filter(country => country.name.toLowerCase().indexOf(show.toLowerCase()) >= 0)

  const handleFilterChange = (event) => {
    setShow(event.target.value)
  }

  return (
    <div>
      <Filter filterChange={handleFilterChange} />
      <Countries filtered={activeFilter} />
    </div>
  )

}

export default App
